import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:ecommerce_app/providers/cart_provider.dart';
import 'package:ecommerce_app/screens/cart_screen.dart';
import 'package:ecommerce_app/screens/product_detail_screen.dart';
import 'package:ecommerce_app/widgets/product_card.dart';
import 'package:firebase_auth/firebase_auth.dart';
import 'package:flutter/material.dart';
import 'package:provider/provider.dart';

class HomeScreen extends StatefulWidget {
  const HomeScreen({super.key});

  @override
  State<HomeScreen> createState() => _HomeScreenState();
}

class _HomeScreenState extends State<HomeScreen> {
  final CollectionReference _productsCollection =
  FirebaseFirestore.instance.collection('products');

  void _logout() async {
    await FirebaseAuth.instance.signOut();
    Navigator.of(context).pushReplacementNamed('/login'); // go to login
    ScaffoldMessenger.of(context).showSnackBar(
      const SnackBar(content: Text("Logged out successfully")),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Fitness Supplements Store'),
        actions: [
          Consumer<CartProvider>(
            builder: (context, cart, child) {
              return Badge(
                label: Text(cart.itemCount.toString()),
                isLabelVisible: cart.itemCount > 0,
                child: IconButton(
                  icon: const Icon(Icons.shopping_cart),
                  onPressed: () {
                    Navigator.of(context).push(
                      MaterialPageRoute(
                        builder: (context) => const CartScreen(),
                      ),
                    );
                  },
                ),
              );
            },
          ),
          IconButton(
            icon: const Icon(Icons.logout),
            onPressed: _logout,
          ),
        ],
      ),
      body: StreamBuilder<QuerySnapshot>(
        stream: _productsCollection.snapshots(),
        builder: (context, snapshot) {
          if (snapshot.connectionState == ConnectionState.waiting) {
            return const Center(child: CircularProgressIndicator());
          }

          if (snapshot.hasData && snapshot.data!.docs.isNotEmpty) {
            final products = snapshot.data!.docs;
            return _buildProductGrid(products);
          }

          final demoProducts = [
            {
              'name': 'Optimum Nutrition Whey Protein',
              'price': 4842.00,
              'description': '24g whey protein per serving, low sugar & fat, 5.5g BCAA.',
              'imageUrl': 'https://th.bing.com/th/id/OIP.5TnsLyjRB_b0yopazf2qDQHaE8?w=261&h=180&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3',
            },
            {
              'name': 'Creatine Monohydrate',
              'price': 1160.00,
              'description': 'Micronised creatine to boost strength & power.',
              'imageUrl': 'data:image/webp;base64,',
            },
            {
              'name': 'BCAA Powder',
              'price': 1890.00,
              'description': 'Essential amino acids for muscle recovery and growth.',
              'imageUrl': 'https://th.bing.com/th/id/OIP.jppuDBHwBpBiM0oTdt8HlAHaHa?w=167&h=180&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3',
            },
            {
              'name': 'L-Glutamine',
              'price': 1450.00,
              'description': 'Supports muscle recovery and reduces soreness after workouts.',
              'imageUrl': 'https://th.bing.com/th?id=OIF.lH%2biSL2gQr2ykMbHbTEIpQ&w=196&h=196&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3',
            },
            {
              'name': 'Pre-Workout Booster',
              'price': 1980.00,
              'description': 'Energy, focus, and endurance for intense training.',
              'imageUrl': 'data:image/webp;base64,',
            },
            {
              'name': 'Fish Oil Capsules',
              'price': 950.00,
              'description': 'Supports heart, brain, and joint health with Omega-3.',
              'imageUrl': 'https://th.bing.com/th/id/OIP.IX7pUvhoMcBliRcRes7fjAHaHa?w=174&h=180&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3',
            },
            {
              'name': 'Multivitamins',
              'price': 1200.00,
              'description': 'Essential daily vitamins for overall health.',
              'imageUrl': 'data:image/webp;base64,',
            },
            {
              'name': 'Casein Protein',
              'price': 3600.00,
              'description': 'Slow-digesting protein for night-time muscle recovery.',
              'imageUrl': 'data:image/webp;base64,',
            },
            {
              'name': 'Weight Gainer',
              'price': 4100.00,
              'description': 'High-calorie protein for lean mass gain.',
              'imageUrl': 'https://th.bing.com/th/id/OIP.6wRU1HBbEA4inbZ8OPdARQHaJQ?w=129&h=180&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3',
            },
            {
              'name': 'Beta-Alanine',
              'price': 1300.00,
              'description': 'Improves performance by reducing fatigue during intense workouts.',
              'imageUrl': 'data:image/webp;base64,',
            },
          ];

          return _buildDemoGrid(demoProducts);
        },
      ),
    );
  }

  Widget _buildProductGrid(List<QueryDocumentSnapshot> products) {
    return GridView.builder(
      padding: const EdgeInsets.all(10),
      gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
        crossAxisCount: 2,
        childAspectRatio: 0.75,
        crossAxisSpacing: 10,
        mainAxisSpacing: 10,
      ),
      itemCount: products.length,
      itemBuilder: (context, index) {
        final product = products[index];
        final productData = product.data() as Map<String, dynamic>;

        return ProductCard(
          productData: productData,
          onTap: () {
            Navigator.push(
              context,
              MaterialPageRoute(
                builder: (context) => ProductDetailScreen(
                  productData: productData,
                  productId: product.id,
                ),
              ),
            );
          },
        );
      },
    );
  }

  Widget _buildDemoGrid(List<Map<String, dynamic>> demoProducts) {
    return GridView.builder(
      padding: const EdgeInsets.all(10),
      gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
        crossAxisCount: 2,
        childAspectRatio: 0.75,
        crossAxisSpacing: 10,
        mainAxisSpacing: 10,
      ),
      itemCount: demoProducts.length,
      itemBuilder: (context, index) {
        final product = demoProducts[index];
        return ProductCard(
          productData: product,
          onTap: () {
            Navigator.push(
              context,
              MaterialPageRoute(
                builder: (context) => ProductDetailScreen(
                  productData: product,
                  productId: 'demo_${index + 1}',
                ),
              ),
            );
          },
        );
      },
    );
  }
}
